
public class AulaLOOP {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int contador=0;
       while(contador<=10) {
    	   System.out.println(contador);
    	   //contador=contador+1; a sintax debaixo corresponde a mesma coisa que o comentario aqui
    	   //contador+=1; a sintex debaixo corresponde a mesma coisa, a variavel ela mais 1
    	   contador++;
       }
	}

}
